mapper.select:
select

  distinct

'true' as QUERYID,

from t_product




  order by ${orderByClause}


  limit #{limit} offset #{offset}

SELECT t1.*, t2.id detail_id, t2.product_id detail_product_id, t2.size_type detail_size_type, t2.order_num detail_order_num, t2.warehouse_num detail_warehouse_num, t2.color detail_color, t2.create_time detail_create_time, t2.update_time detail_update_time, t2.creater detail_creater, t2.modifier detail_modifier, t2.remarks detail_remarks FROM ( select * from t_product
order by ${orderByClause}
limit #{limit} offset #{offset}
) t1 left join t_product_detail t2 on (t1.id = t2.product_id and t2.is_deleted = 0) order by t1.create_time desc, t2.size_type desc
